-- Name: vector_similarity(text, text); Type: FUNCTION; Schema: public; Owner: app_user
--

CREATE FUNCTION public.vector_similarity(vec1 text, vec2 text) RETURNS double precision
    LANGUAGE plpgsql
    AS $$
            DECLARE
                arr1 float[];
                arr2 float[];
                dot_product float := 0;
                norm1 float := 0;
                norm2 float := 0;
                i int;
            BEGIN
                -- Parse JSON arrays to float arrays
                arr1 := array_to_float(vec1::json);
                arr2 := array_to_float(vec2::json);
                
                -- Calculate cosine similarity
                FOR i IN 1..array_length(arr1, 1) LOOP
                    dot_product := dot_product + arr1[i] * arr2[i];
                    norm1 := norm1 + arr1[i] * arr1[i];
                    norm2 := norm2 + arr2[i] * arr2[i];
                END LOOP;
                
                -- Avoid division by zero
                IF norm1 = 0 OR norm2 = 0 THEN
                    RETURN 0;
                END IF;
                
                RETURN dot_product / (sqrt(norm1) * sqrt(norm2));
            END;
            $$;


